mapper.update:

	update board set title = #{title},
                   content = #{content}
  where no = #{no}

mapper.select:
select ort.rn, ort.no, ort.title, ort.content, ort.hit, to_char(ort.reg_date, 'YYYY-MM-DD HH:MI') regDate, ort.user_no, ort.name user_name from ( select rownum rn, ot.no, ot.title, ot.content, ot.hit, ot.reg_date, ot.user_no, ot.name from ( select b.no, b.title, b.content, b.hit, b.reg_date, b.user_no, u.name from board b, users u where b.user_no = u.no
and b.title like '%'||#{keyword}||'%'
order by no desc ) ot ) ort where rn>= #{startRnum} and rn<= #{endRnum}
